function llh = enu2llh( enu,orgllh)
%     orgxyz = xyz2llh(orgllh);
    xyz=enu2xyz(enu,orgllh);
    llh=[];
    for  i=1:size(xyz,1)
        llh(i,:)=xyz2llh(xyz(i,:));
    end
end
